files = glob("/Users/guillaume/Documents/Travail/2020-2021/data_science/Projets/DataScienceP1/Dataset/processed/*.txt");linkMatrix = zeros(numel(files),numel(files));for i=1:numel(files)  currentFile = textread (files{i}, "%s");  linkFrom = str2num(substr(files{i},rindex(files{i},"/")+5,rindex(files{i},".")-(rindex(files{i},"/")+5)));  for j =1:numel(currentFile)      currentWord = currentFile{j};      if  isempty(regexp(currentWord,"linkTo:.*")) == 0          linkTo = str2num( substr(currentWord,index(currentWord,":")+5,index(currentWord,".")-(index(currentWord,":")+5)));          if linkTo != linkFrom                linkMatrix(linkFrom,linkTo)=1;           endif      endif      endforendfordisp(linkMatrix)